		"movies/sap/com/view/Detail.view.xml": "<mvc:View\n\tcontrollerName=\"movies.sap.com.controller.Detail\"\n\txmlns=\"sap.m\"\n\txmlns:mvc=\"sap.ui.core.mvc\"\n\txmlns:semantic=\"sap.m.semantic\"\n\txmlns:l=\"sap.ui.layout\"\n\txmlns:footerbar=\"sap.ushell.ui.footerbar\">\n\n\t<semantic:DetailPage\n\t\tid=\"page\"\n\t\tnavButtonPress=\"onNavBack\"\n\t\tshowNavButton=\"{device>/system/phone}\"\n\t\ttitle=\"{i18n>detailTitle}\"\n\t\tbusy=\"{detailView>/busy}\"\n\t\tbusyIndicatorDelay=\"{detailView>/delay}\">\n\t\t<semantic:positiveAction>\n\t        <semantic:PositiveAction id=\"approveButton\" text=\"Rate Movie\" press=\"onApprove\" />\n\t   </semantic:positiveAction>\n\t\t<semantic:content>\n\t\t\t<ObjectHeader\n\t\t\t\tid=\"objectHeader\"\n\t\t\t\ttitle=\"{TITLE}\"\n\t\t\t\tnumber=\"{\n\t\t\t\t\tpath: 'YEAR'\n\t\t\t\t}\"\n>\n\t\t\t</ObjectHeader>\n\t\t\t\n\t\t\t<FlexBox\n\t\t\t\t\theight=\"500px\"\n\t\t\t\t\talignItems=\"Start\"\n\t\t\t\t\tjustifyContent=\"Center\">\n\t\t\t\t\t<items>\n\t\t\t\t\t\t<Image height=\"500px\" src=\"{POSTER}\" />\n\t\t\t\t\t</items>\n\t\t\t\t</FlexBox>\n\n\t\t\t<Table\n\t\t\t\tid=\"ratingsTable\"\n\t\t\t\twidth=\"auto\"\n\t\t\t\titems=\"{Ratings}\"\n\t\t\t\tupdateFinished=\"onListUpdateFinished\"\n\t\t\t\tnoDataText=\"{i18n>detailLineItemTableNoDataText}\"\n\t\t\t\tbusyIndicatorDelay=\"{detailView>/lineItemTableDelay}\"\n\t\t\t\tclass=\"sapUiResponsiveMargin\">\n\t\t\t\t<headerToolbar>\n\t\t\t\t\t<Toolbar id=\"RatingsToolbar\">\n\t\t\t\t\t\t<Title\n\t\t\t\t\t\t\tid=\"RatingsHeader\"\n\t\t\t\t\t\t\ttext=\"Ratings\"/>\n\t\t\t\t\t</Toolbar>\n\t\t\t\t</headerToolbar>\n\t\t\t\t<columns>\n\t\t\t\t\t<Column>\n\t\t\t\t\t\t<Text text=\"User\"/>\n\t\t\t\t\t</Column>\n\t\t\t\t\t<Column\n\t\t\t\t\t\tminScreenWidth=\"Tablet\"\n\t\t\t\t\t\tdemandPopin=\"true\"\n\t\t\t\t\t\thAlign=\"Right\">\n\t\t\t\t\t\t<Text text=\"Ratings\"/>\n\t\t\t\t\t</Column>\n\t\t\t\t</columns>\n\t\t\t\t<items>\n\t\t\t\t\t<ColumnListItem>\n\t\t\t\t\t\t<cells>\n\t\t\t\t\t\t\t<ObjectIdentifier\n\t\t\t\t\t\t\t\ttitle=\"{USER}\"/>\n\t\t\t\t\t\t\t<RatingIndicator value=\"{RATING}\" />\n\t\t\t\t\t\t</cells>\n\t\t\t\t\t</ColumnListItem>\n\t\t\t\t</items>\n\t\t\t</Table>\n\t\t\t\n\t\t\t\n\t\t\t<Table\n\t\t\t\tid=\"lineItemsList\"\n\t\t\t\twidth=\"auto\"\n\t\t\t\titems=\"{Actors}\"\n\t\t\t\tupdateFinished=\"onListUpdateFinished\"\n\t\t\t\tnoDataText=\"{i18n>detailLineItemTableNoDataText}\"\n\t\t\t\tbusyIndicatorDelay=\"{detailView>/lineItemTableDelay}\"\n\t\t\t\tclass=\"sapUiResponsiveMargin\">\n\t\t\t\t<headerToolbar>\n\t\t\t\t\t<Toolbar id=\"lineItemsToolbar\">\n\t\t\t\t\t\t<Title\n\t\t\t\t\t\t\tid=\"lineItemsHeader\"\n\t\t\t\t\t\t\ttext=\"{detailView>/lineItemListTitle}\"/>\n\t\t\t\t\t</Toolbar>\n\t\t\t\t</headerToolbar>\n\t\t\t\t<columns>\n\t\t\t\t\t<Column>\n\t\t\t\t\t\t<Text text=\"{i18n>detailLineItemTableIDColumn}\"/>\n\t\t\t\t\t</Column>\n\t\t\t\t\t<Column\n\t\t\t\t\t\tminScreenWidth=\"Tablet\"\n\t\t\t\t\t\tdemandPopin=\"true\"\n\t\t\t\t\t\thAlign=\"Right\">\n\t\t\t\t\t\t<Text text=\"{i18n>detailLineItemTableUnitNumberColumn}\"/>\n\t\t\t\t\t</Column>\n\t\t\t\t</columns>\n\t\t\t\t<items>\n\t\t\t\t\t<ColumnListItem>\n\t\t\t\t\t\t<cells>\n\t\t\t\t\t\t\t<ObjectIdentifier\n\t\t\t\t\t\t\t\ttitle=\"{ACTOR}\"\n\t\t\t\t\t\t\t\ttext=\"{ACTOR}\"/>\n\t\t\t\t\t\t\t<ObjectNumber\n/>\n\t\t\t\t\t\t</cells>\n\t\t\t\t\t</ColumnListItem>\n\t\t\t\t</items>\n\t\t\t</Table>\n\t\t\t\n\t\t<Dialog id=\"approvalDialog\" title=\"{approvalProperties>/approvalTitle}\" class=\"sapUiContentPadding\">\n\t\t<content>\n\t\t\t<l:VerticalLayout id=\"textLayout\" width=\"100%\">\n\t\t\t\t<Input id=\"user\" placeholder=\"Please rate the movie {TITLE}\" />\n\t\t\t\t<RatingIndicator id=\"rating\" />\n\t\t\t</l:VerticalLayout>\n\t\t</content>\t\n\t\t<beginButton>\n\t\t\t<Button id=\"confirmButton\" text=\"Confirm\" press=\"onConfirmAction\" />\n\t\t</beginButton>\n\t\t<endButton>\n\t\t\t<Button id=\"cancelButton\" text=\"Cancel\" press=\"onCancelAction\" />\n\t\t</endButton>\n\t</Dialog>\t\t\n\t\t</semantic:content>\n\n\t\t<semantic:sendEmailAction>\n\t\t\t<semantic:SendEmailAction\n\t\t\t\tid=\"shareEmail\"\n\t\t\t\tpress=\"onShareEmailPress\"/>\n\t\t</semantic:sendEmailAction>\n\n\t\t<semantic:shareInJamAction>\n\t\t\t<semantic:ShareInJamAction\n\t\t\t\tid=\"shareInJam\"\n\t\t\t\tvisible=\"{FLP>/isShareInJamActive}\"\n\t\t\t\tpress=\"onShareInJamPress\"/>\n\t\t</semantic:shareInJamAction>\n\n\t\t<semantic:saveAsTileAction>\n\t\t\t<footerbar:AddBookmarkButton\n\t\t\t\tid=\"shareTile\"\n\t\t\t\ttitle=\"{detailView>/saveAsTileTitle}\"/>\n\t\t</semantic:saveAsTileAction>\n\n\t</semantic:DetailPage>\n\n</mvc:View>",
