		"movies/sap/com/view/Master.view.xml": "<mvc:View\n\tcontrollerName=\"movies.sap.com.controller.Master\"\n\txmlns:mvc=\"sap.ui.core.mvc\"\n\txmlns:core=\"sap.ui.core\"\n\txmlns=\"sap.m\"\n\txmlns:semantic=\"sap.m.semantic\">\n\n\t<semantic:MasterPage\n\t\tid=\"page\"\n\t\ttitle=\"{masterView>/title}\"\n\t\tnavButtonPress=\"onNavBack\"\n\t\tshowNavButton=\"true\">\n\t\t<semantic:subHeader>\n\t\t\t<Bar id=\"headerBar\">\n\t\t\t\t<contentMiddle>\n\t\t\t\t\t<SearchField\n\t\t\t\t\t\tid=\"searchField\"\n\t\t\t\t\t\tshowRefreshButton=\"{= !${device>/support/touch} }\"\n\t\t\t\t\t\ttooltip=\"{i18n>masterSearchTooltip}\"\n\t\t\t\t\t\twidth=\"100%\"\n\t\t\t\t\t\tsearch=\"onSearch\">\n\t\t\t\t\t</SearchField>\n\t\t\t\t</contentMiddle>\n\t\t\t</Bar>\n\t\t</semantic:subHeader>\n\n\t\t<semantic:content>\n\t\t\t<PullToRefresh\n\t\t\t\tid=\"pullToRefresh\"\n\t\t\t\tvisible=\"{device>/support/touch}\"\n\t\t\t\trefresh=\"onRefresh\" />\n\t\t\t<!-- For client side filtering add this to the items attribute:  parameters: {operationMode: 'Client'}}\" -->\n\t\t\t<List\n\t\t\t\tid=\"list\"\n\t\t\t\titems=\"{\n\t\t\t\t\tpath: '/Movies',\n\t\t\t\t\tsorter: {\n\t\t\t\t\t\tpath: 'TITLE',\n\t\t\t\t\t\tdescending: false\n\t\t\t\t\t\t},\n\t\t\t\t\tgroupHeaderFactory: '.createGroupHeader'\n\t\t\t\t}\"\n\t\t\t\tbusyIndicatorDelay=\"{masterView>/delay}\"\n\t\t\t\tnoDataText=\"{masterView>/noDataText}\"\n\t\t\t\tmode=\"{= ${device>/system/phone} ? 'None' : 'SingleSelectMaster'}\"\n\t\t\t\tgrowing=\"true\"\n\t\t\t\tgrowingScrollToLoad=\"true\"\n\t\t\t\tupdateFinished=\"onUpdateFinished\"\n\t\t\t\tselectionChange=\"onSelectionChange\">\n\t\t\t\t<infoToolbar>\n\t\t\t\t\t<Toolbar\n\t\t\t\t\t\tactive=\"true\"\n\t\t\t\t\t\tid=\"filterBar\"\n\t\t\t\t\t\tvisible=\"{masterView>/isFilterBarVisible}\"\n\t\t\t\t\t\tpress=\"onOpenViewSettings\">\n\t\t\t\t\t\t<Title\n\t\t\t\t\t\t\tid=\"filterBarLabel\"\n\t\t\t\t\t\t\ttext=\"{masterView>/filterBarLabel}\" />\n\t\t\t\t\t</Toolbar>\n\t\t\t\t</infoToolbar>\n\t\t\t\t<items>\n\t\t\t\t\t<ObjectListItem\n\t\t\t\t\t\ttype=\"{= ${device>/system/phone} ? 'Active' : 'Inactive'}\"\n\t\t\t\t\t\tpress=\"onSelectionChange\"\n\t\t\t\t\t\ttitle=\"{TITLE}\"\n\t\t\t\t\t\tnumber=\"{\n\t\t\t\t\t\t\tpath: 'YEAR'\n\t\t\t\t\t\t}\"\n>\n\t\t\t\t\t</ObjectListItem>\n\t\t\t\t</items>\n\t\t\t</List>\n\t\t</semantic:content>\n\n\t\t<semantic:sort>\n\t\t\t<semantic:SortSelect\n\t\t\t\tid=\"sort\"\n\t\t\t\tselectedKey=\"{masterView>/sortBy}\"\n\t\t\t\tchange=\"onSort\">\n\t\t\t\t<core:Item\n\t\t\t\t\tid=\"masterSort1\"\n\t\t\t\t\tkey=\"TITLE\"\n\t\t\t\t\ttext=\"{i18n>masterSort1}\"/>\n\t\t\t\t<core:Item\n\t\t\t\t\tid=\"masterSort2\"\n\t\t\t\t\tkey=\"YEAR\"\n\t\t\t\t\ttext=\"{i18n>masterSort2}\"/>\n\t\t\t</semantic:SortSelect>\n\t\t</semantic:sort>\n\n\t\t<semantic:filter>\n\t\t\t<semantic:FilterAction\n\t\t\t\tid=\"filter\"\n\t\t\t\tpress=\"onOpenViewSettings\" />\n\t\t</semantic:filter>\n\n\t\t<semantic:group>\n\t\t\t<semantic:GroupSelect\n\t\t\t\tid=\"group\"\n\t\t\t\tselectedKey=\"{masterView>/groupBy}\"\n\t\t\t\tchange=\"onGroup\">\n\t\t\t\t<core:Item\n\t\t\t\t\tid=\"masterGroupNone\"\n\t\t\t\t\tkey=\"None\"\n\t\t\t\t\ttext=\"{i18n>masterGroupNone}\"/>\n\t\t\t\t<core:Item\n\t\t\t\t\tid=\"masterGroup1\"\n\t\t\t\t\tkey=\"YEAR\"\n\t\t\t\t\ttext=\"{i18n>masterGroup1}\"/>\n\t\t\t</semantic:GroupSelect>\n\t\t</semantic:group>\n\n\t</semantic:MasterPage>\n\n</mvc:View>"
